def get_plugin_info():
    return Plugin('cpedeployment', '3.6.7')

from com.anuta.service.python.plugin import PythonPlugin
from com.anuta.service.python.plugin import PythonPluginType

from servicemodel import util
import cpedeployment
from cpedeployment_lib import log

class Plugin(PythonPlugin):
    """Class to register cpedeployment plugin to NCX
    """
    def __init__(self, name, version):
        self.setName(name)
        self.setVersion(version)
        self.setPluginType(PythonPluginType.SERVICE_MODEL)
        self.setDescription('CPEDEPLOYMENTService Plugin')

    def init(self):
        log('registering cpedeployment')
        cpedeployment.CPEDEPLOYMENTService.getInstance().register()

    def shutdown(self):
        log('unregistering cpedeployment')
        cpedeployment.CPEDEPLOYMENTService.getInstance().unregister()
